(async()=>{const inProgress=scriptLive=>{let unixTimestamp;if(scriptLive){unixTimestamp=getLocalStorage(StorageIds.globalData).time2}else{const currentTime=Math.floor(Date.now()/1e3);const currentDate=new Date;currentDate.setHours(23,59,59,999);unixTimestamp=Math.floor(currentDate.getTime()/1e3)}function updateCountdown(){const now=Math.floor(Date.now()/1e3);const timeRemaining=unixTimestamp-now;if(timeRemaining<0){clearInterval(countdownInterval);document.getElementById("countdown").textContent=scriptLive?"DailyTroops will soon be recruited":"Script Will be"+" live a few moments.";return}const hours=Math.floor(timeRemaining/3600);const minutes=Math.floor(timeRemaining%3600/60);const seconds=timeRemaining%60;const time=hours+"hours, "+minutes+"minutes"+seconds+" seconds";document.getElementById("countdown").textContent=(scriptLive?"Next daily troops in : ":"Script will be released in: ")+time}const countdownInterval=setInterval(updateCountdown,1e3)};inProgress(globalData.released)})();